mapper.select:
SELECT COUNT(*)
FROM user
WHERE email = #{email}

SELECT COUNT(*)
FROM user
WHERE nickName = #{nickName}

SELECT COUNT(*)
FROM user
WHERE interest = #{interest}

SELECT COUNT(*)
FROM user
WHERE country = #{country}

SELECT COUNT(*)
FROM user
WHERE type = #{type}

mapper.update:
UPDATE user
SET password = #{password}
WHERE email = #{email}
